 * @returns {boolean} warned
 */
export const warnNoPromiseSupport = source => {
  /* istanbul ignore else */
  if (HAS_PROMISE_SUPPORT) {
    return false
  } else {
    warn(`${source}: Requires Promise support.`)
    return true
  }
}

/**
