export const oneYearAgo = date => {
  return addYears(date, -1)
}

export const oneYearAhead = date => {
  return addYears(date, 1)
}

export const oneDecadeAgo = date => {
  return addYears(date, -10)
}

export const oneDecadeAhead = date => {
  return addYears(date, 10)
}

// Helper function to constrain a date between two values
// Always returns a `Date` object or `null` if no date passed
export const constrainDate = (date, min = null, max = null) => {
  // Ensure values are `Date` objects (or `null`)
  date = parseYMD(date)
  min = parseYMD(min) || date
  max = parseYMD(max) || date
  // Return a new `Date` object (or `null`)
  return date ? (date < min ? min : date > max ? max : date) : null
}
// Localization utilities
import { RX_STRIP_LOCALE_MODS } from '../constants/regex'
import { arrayIncludes } from './array'
import { toString } from './string'

// Languages that are RTL
const RTL_LANGS = [
  'ar',
  'az',
  'ckb',
  'fa',
  'he',
  'ks',
  'lrc',
  'mzn',
  'ps',
  'sd',
  'te',
  'ug',
  'ur',
  'yi'
].map(locale => locale.toLowerCase())

// Returns true if the locale is RTL
export const isLocaleRTL = locale => {
  // Determines if the locale is RTL (only single locale supported)
  const parts = toString(locale)
    .toLowerCase()
    .replace(RX_STRIP_LOCALE_MODS, '')
    .split('-')
  const locale1 = parts.slice(0, 2).join('-')
  const locale2 = parts[0]
  return arrayIncludes(RTL_LANGS, locale1) || arrayIncludes(RTL_LANGS, locale2)
}
